import {useQuery} from 'react-query';

const fetchData = async () =>
{
    const response = await fetch('https://jsonplaceholder.typicode.com/posts');
    return response.json(); 
}
 
const usePosts = ()=>{
    return useQuery('posts',fetchData);
}

export default usePosts;
